c_parser_context_t *c_parser_create(c_parser_state *auxil) {
    return pcc_context__create(auxil);
}

int c_parser_parse(c_parser_context_t *ctx, c_parser_ast_node **ret) {
    if (pcc_apply_rule(ctx, pcc_evaluate_rule_program, &ctx->thunks, ret))
        pcc_do_action(ctx, &ctx->thunks, ret);
    else
        PCC_ERROR(ctx->auxil);
    pcc_commit_buffer(ctx);
    pcc_thunk_array__revert(ctx->auxil, &ctx->thunks, 0);
    return pcc_refill_buffer(ctx, 1) >= 1;
}

void c_parser_destroy(c_parser_context_t *ctx) {
    pcc_context__destroy(ctx);
}

// Returns parse state after parsing source text.
c_parser_state c_parser_execute(char *source) {
  c_parser_state state = c_parser_state_init(source);
  c_parser_context_t *ctx = c_parser_create(&state);
  c_parser_ast_node *ast;
  int ret = c_parser_parse(ctx, &ast);
  state.programNode = ast;
  c_parser_destroy(ctx);
  if (ret) {
    state.isValid = false;
  }
  return state;
}